package com.kodnest.numbers;

public class EvenSum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int even = 0;
		int odd = 0;
		int rem = 0;
		if (rem%2==0) {
			 even=even+rem;
			System.out.println(even);
		}else {
		    odd=odd+rem;
			System.out.println(odd);
			
		}

	}

}
